SET NAMES UTF8;
DROP DATABASE IF EXISTS WM;
CREATE DATABASE WM CHARSET=UTF8;
USE WM;

/*用户表，新闻表，产品表，购物车表，购物车详情表*/
/*用户表*/
CREATE TABLE wm_user(
    uid INT PRIMARY KEY AUTO_INCREMENT,
    uname VARCHAR(32),/*用户名*/
    upwd VARCHAR(32),/*密码*/
    phone VARCHAR(32)/*电话*/
);
